const VOW_LOCKON = 67228964

module.exports = function Lockons(dispatch) {
	dispatch.hook('cCanLockonTarget', event => {
		if(event.skill == VOW_LOCKON) return

		dispatch.toClient('sCanLockonTarget', Object.assign({ ok: true }, event))
	})

	dispatch.hook('sCanLockonTarget', event => {
		if(event.skill == VOW_LOCKON) return

		if(!event.ok)
			console.log('[Error] Lockon prediction failed: ' + event.skill)

		return false
	})
}