const RED_MOTE_THROW = 18,
	BLUE_MOTE_THROW = 22

module.exports = function NoMoteCancel(dispatch) {
	let job = -1

	dispatch.hook('S_LOGIN', 1, event => { job = (event.model - 10101) % 100 })

	dispatch.hook('C_CANCEL_SKILL', 1, event => {
		if(job == 7) {
			let skill = Math.floor((event.skill - 0x4000000) / 10000)

			if(skill == RED_MOTE_THROW || skill == BLUE_MOTE_THROW) return false
		}
	})
}