module.exports = function Notifier(dispatch) {
  let username = '';

  dispatch.hook('sLogin', (event) => {
    username = '' + event.name;
  });

  dispatch.hook('sPrivateChat', (event) => {
    if(username === ("" + event.authorName)) {
      let message = event.message.replace('<FONT>', '').replace('</FONT>', '');
      console.log('Message: ' + message);
      if(message.startsWith('-')) {
        dispatch.toServer('cChat', {
          channel: 21,
          message: message.substr(1),
        });
      }
    }
  });
}
