module.exports = function TimeStamps(dispatch) { 
	 
	function now() { 
		return new Date().toLocaleTimeString().replace(/([\d]+:[\d]{2})(:[\d]{2})(.*)/, "$1$3")
	};
	
	dispatch.hook('sChat', (event) => {
		if (event.channel !== 9 && event.channel !== 26) { // Emotes and Greets shouldnt have timestamps (?)
			event.authorName = now() + "][" + event.authorName;
		}
		return true;
	});
}